// src/pages/CadastroPage.jsx
import React, { useState } from "react";

export default function CadastroPage({ onCadastroSucesso }) {
  const [form, setForm] = useState({
    nome: "",
    email: "",
    telefone: "",
    telefoneEmergencia: "",
    senha: "",
  });
  const [erro, setErro] = useState("");
  const [sucesso, setSucesso] = useState(false);
  const [carregando, setCarregando] = useState(false);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setErro("");
    setSucesso(false);
    setCarregando(true);

    // Validação simples
    if (!form.nome || !form.email || !form.telefone || !form.telefoneEmergencia || !form.senha) {
      setErro("Preencha todos os campos.");
      setCarregando(false);
      return;
    }

    // Chamar backend
    try {
      const resp = await fetch("http://localhost:3000/cadastro", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await resp.json();

      if (resp.ok) {
        setSucesso(true);
        setErro("");
        if (onCadastroSucesso) onCadastroSucesso();
      } else {
        setErro(data.erro || "Erro ao cadastrar.");
      }
    } catch (e) {
      setErro("Erro ao conectar com o servidor.");
    }
    setCarregando(false);
  }

  return (
    <div className="max-w-md mx-auto bg-white rounded-2xl shadow-xl p-8 mt-10">
      <h1 className="text-2xl font-bold mb-6 text-center">Criar Conta</h1>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="nome"
          placeholder="Nome completo"
          value={form.nome}
          onChange={handleChange}
          className="w-full border rounded-lg px-3 py-2"
          autoFocus
        />
        <input
          name="email"
          type="email"
          placeholder="E-mail"
          value={form.email}
          onChange={handleChange}
          className="w-full border rounded-lg px-3 py-2"
        />
        <input
          name="telefone"
          placeholder="Telefone"
          value={form.telefone}
          onChange={handleChange}
          className="w-full border rounded-lg px-3 py-2"
        />
        <input
          name="telefoneEmergencia"
          placeholder="Telefone de emergência"
          value={form.telefoneEmergencia}
          onChange={handleChange}
          className="w-full border rounded-lg px-3 py-2"
        />
        <input
          name="senha"
          type="password"
          placeholder="Senha"
          value={form.senha}
          onChange={handleChange}
          className="w-full border rounded-lg px-3 py-2"
        />
        <button
          type="submit"
          disabled={carregando}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 rounded-xl mt-2"
        >
          {carregando ? "Cadastrando..." : "Cadastrar"}
        </button>
      </form>

      {erro && <div className="mt-4 text-red-600 text-center">{erro}</div>}
      {sucesso && (
        <div className="mt-4 text-green-600 text-center">
          Cadastro realizado! Redirecionando...
        </div>
      )}
    </div>
  );
}
