// src/pages/ChatPage.jsx
import { useState, useRef, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ChatBubble from "../components/ChatBubble";

const alanAvatar = "https://ui-avatars.com/api/?name=Alan+Fernandes&background=0AE68A&color=fff";

// Telinha de sess√£o encerrada
function SessionEnded({ onNovoChat, onVerSessoes }) {
  return (
    <div className="flex flex-col items-center justify-center h-[70vh] bg-white rounded-2xl shadow-lg mt-8 max-w-2xl mx-auto text-center p-8">
      <h2 className="text-2xl font-semibold mb-2">Sess√£o encerrada ‚úÖ</h2>
      <p className="text-gray-600 mb-6">
        Obrigado pela conversa! Quer come√ßar um novo chat agora ou rever o hist√≥rico?
      </p>
      <div className="flex gap-3">
        <button
          onClick={onNovoChat}
          className="bg-emerald-500 hover:bg-emerald-600 text-white rounded-xl px-4 py-2 font-medium transition"
        >
          Novo chat
        </button>
        <button
          onClick={onVerSessoes}
          className="bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-xl px-4 py-2 font-medium transition"
        >
          Ver sess√µes
        </button>
      </div>
      <div className="mt-6 text-sm text-gray-400">See you later, alligator! üêä</div>
    </div>
  );
}

export default function ChatPage(props) {
  const navigate = useNavigate();
  const { sessaoId } = useParams();

  // Prioriza: URL > props > localStorage
  const user_id = props.user_id || localStorage.getItem("user_id");
  const user_name = props.user_name || localStorage.getItem("user_name") || "Voc√™";
  const sessao_id = sessaoId || props.sessao_id || localStorage.getItem("sessao_id");

  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [erro, setErro] = useState("");
  const [digitando, setDigitando] = useState(false);
  const [ended, setEnded] = useState(false); // <<< NOVO: estado de sess√£o encerrada
  const endRef = useRef(null);

  // Guarda sessao_id atual
  useEffect(() => {
    if (sessao_id) {
      localStorage.setItem("sessao_id", sessao_id);
    }
  }, [sessao_id]);

  // Scroll
  useEffect(() => {
    endRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, digitando]);

  // Carrega hist√≥rico
  useEffect(() => {
    if (!sessao_id) return;
    setLoading(true);
    fetch(`http://localhost:3000/historico/${sessao_id}`)
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data.mensagens)) {
          setMessages(
            data.mensagens.map(msg => ({
              name: msg.origem === "usuario" ? user_name : "Alan",
              text: msg.texto_mensagem,
              isUser: msg.origem === "usuario",
              avatar: msg.origem === "usuario" ? undefined : alanAvatar,
            }))
          );
        } else {
          setMessages([]);
        }
      })
      .catch(() => setErro("Erro ao carregar mensagens"))
      .finally(() => setLoading(false));
  }, [sessao_id, user_name]);

  // Enviar mensagem
  const sendMessage = async () => {
    if (ended) return; // <<< se sess√£o encerrada, n√£o envia
    if (input.trim() === "" || !sessao_id || !user_id) return;
    setErro("");
    setMessages(msgs => [...msgs, { name: user_name, text: input, isUser: true }]);
    const userText = input;
    setInput("");
    setDigitando(true);

    try {
      // Salva mensagem do usu√°rio
      await fetch("http://localhost:3000/mensagem", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          sessao_id,
          user_id,
          texto_mensagem: userText,
          origem: "usuario"
        })
      });

      // Chama IA
      const resp = await fetch("http://localhost:3000/ia", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sessao_id, user_id, mensagem: userText })
      });
      const data = await resp.json();
      if (resp.ok && data.resposta) {
        setMessages(msgs => [
          ...msgs,
          { name: "Alan", text: data.resposta, isUser: false, avatar: alanAvatar }
        ]);
      } else {
        setMessages(msgs => [
          ...msgs,
          { name: "Alan", text: "Ocorreu um erro ao responder. Tente novamente.", isUser: false, avatar: alanAvatar }
        ]);
      }
    } catch (err) {
      setMessages(msgs => [
        ...msgs,
        { name: "Alan", text: "Erro ao conectar com o servidor.", isUser: false, avatar: alanAvatar }
      ]);
    } finally {
      setDigitando(false);
    }
  };

  // <<< NOVO: finalizar sess√£o atual (chama backend e troca UI)
  const handleFinalizarSessao = async () => {
    if (!sessao_id) return;
    try {
      const resp = await fetch("http://localhost:3000/finalizar-sessao", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sessao_id })
      });
      if (!resp.ok) {
        const j = await resp.json().catch(() => ({}));
        throw new Error(j.error || "Falha ao finalizar sess√£o");
      }
      // Marca UI como encerrada
      setEnded(true);
    } catch (e) {
      setErro(e.message || "Erro ao finalizar sess√£o");
    }
  };

  // <<< NOVO: criar nova sess√£o em branco e navegar
  const handleNovoChat = async () => {
    try {
      const uid = user_id || localStorage.getItem("user_id");
      if (!uid) throw new Error("Sem user_id");

      const r = await fetch("http://localhost:3000/sessao", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        // pode ser uma mensagem neutra; o backend usa isso como resumo inicial
        body: JSON.stringify({ user_id: uid, mensagem: "Nova sess√£o iniciada" })
      });
      const j = await r.json();
      if (!r.ok || !j?.sessao?.id) throw new Error(j.erro || "N√£o foi poss√≠vel criar nova sess√£o");

      // salva e navega
      localStorage.setItem("sessao_id", j.sessao.id);
      setEnded(false);
      setMessages([]);
      navigate(`/chat/${j.sessao.id}`);
    } catch (e) {
      setErro(e.message || "Erro ao criar nova sess√£o");
    }
  };

  // <<< NOVO: atalho para ver sess√µes
  const handleVerSessoes = () => {
    navigate("/sessoes");
  };

  // Se encerrou, renderiza a telinha de fim
  if (ended) {
    return <SessionEnded onNovoChat={handleNovoChat} onVerSessoes={handleVerSessoes} />;
  }

  // UI normal do chat
  return (
    <div className="flex flex-col h-[70vh] bg-white rounded-2xl shadow-lg mt-8 max-w-2xl mx-auto">
      {/* √Årea de mensagens */}
      <div className="flex-1 overflow-y-auto p-6">
        {loading && <div className="text-gray-400">Carregando...</div>}
        {messages.map((msg, idx) => (
          <ChatBubble key={idx} {...msg} />
        ))}
        {digitando && (
          <ChatBubble
            text="Alan est√° digitando..."
            isUser={false}
            name="Alan"
            avatar={alanAvatar}
          />
        )}
        <div ref={endRef} />
      </div>

      {/* Rodap√©: input e bot√µes */}
      <div className="border-t p-4 flex items-center gap-2">
        <input
          type="text"
          className="flex-1 border border-emerald-200 rounded-xl px-3 py-2 focus:outline-none focus:ring-2 focus:ring-emerald-400"
          placeholder="Digite sua mensagem..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendMessage()}
          disabled={loading || digitando}
        />
        <button
          onClick={sendMessage}
          className="bg-emerald-500 hover:bg-emerald-600 text-white rounded-xl px-4 py-2 font-medium transition"
          disabled={loading || !input.trim() || digitando}
        >
          Enviar
        </button>

        {/* <<< NOVO: bot√£o de encerrar sess√£o */}
        <button
          onClick={handleFinalizarSessao}
          className="ml-2 bg-gray-100 hover:bg-gray-200 text-gray-700 rounded-xl px-4 py-2 font-medium transition"
          disabled={!sessao_id || loading || digitando}
          title="Gera resumo, fecha a sess√£o e mostra op√ß√µes"
        >
          Encerrar sess√£o
        </button>
      </div>

      {erro && <div className="text-red-500 text-center">{erro}</div>}
    </div>
  );
}
