import { useState, useRef, useEffect } from "react";
import ChatBubble from "../components/ChatBubble";

// Exemplo de avatar (coloque o caminho de uma imagem real depois)
const alanAvatar = "https://ui-avatars.com/api/?name=Alan+Fernandes&background=0AE68A&color=fff"; // Placeholder de avatar

export default function ChatPage() {
  const [messages, setMessages] = useState([
    { name: "Alan", text: "Olá, seja bem-vindo ao Mentor 360! Como posso ajudar você hoje?", isUser: false, avatar: alanAvatar },
    { name: "João", text: "Oi Alan, quero entender como lidar com minha ansiedade no trabalho.", isUser: true },
  ]);
  const [input, setInput] = useState("");
  const endRef = useRef(null);

  // Auto-scroll
  useEffect(() => {
    endRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = () => {
    if (input.trim() === "") return;
    setMessages([...messages, { name: "João", text: input, isUser: true }]);
    setInput("");
    // Depois aqui vai a chamada real à API para obter a resposta do Alan
  };

  return (
    <div className="flex flex-col h-[70vh] bg-white rounded-2xl shadow-lg mt-8 max-w-2xl mx-auto">
      {/* Área de mensagens */}
      <div className="flex-1 overflow-y-auto p-6">
        {messages.map((msg, idx) => (
          <ChatBubble key={idx} {...msg} />
        ))}
        <div ref={endRef} />
      </div>
      {/* Input para nova mensagem */}
      <div className="border-t p-4 flex items-center gap-2">
        <input
          type="text"
          className="flex-1 border border-emerald-200 rounded-xl px-3 py-2 focus:outline-none focus:ring-2 focus:ring-emerald-400"
          placeholder="Digite sua mensagem..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendMessage()}
        />
        <button
          onClick={sendMessage}
          className="bg-emerald-500 hover:bg-emerald-600 text-white rounded-xl px-4 py-2 font-medium transition"
        >
          Enviar
        </button>
      </div>
    </div>
  );
}
