import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import { useState } from "react";

// Menu Superior
function Navbar({ user, onLogout }) {
  return (
    <nav className="w-full bg-white shadow-md py-3 flex justify-between items-center px-6">
      <div className="text-2xl font-bold text-emerald-600">Mentor 360</div>
      <div className="flex gap-6 items-center">
        <Link className="text-gray-700 hover:text-emerald-600 font-medium" to="/">Chat</Link>
        <Link className="text-gray-700 hover:text-emerald-600 font-medium" to="/sessions">Sess�es Anteriores</Link>
        <Link className="text-gray-700 hover:text-emerald-600 font-medium" to="/about">Sobre</Link>
        {user ? (
          <button onClick={onLogout} className="text-rose-600 font-medium ml-4">Sair</button>
        ) : (
          <Link className="text-gray-700 hover:text-emerald-600 font-medium ml-4" to="/login">Login</Link>
        )}
      </div>
    </nav>
  );
}

// P�ginas (depois vamos evoluir cada uma delas)
function ChatPage() {
  return <div className="p-6 text-lg">Chat em constru��o...</div>;
}
function SessionsPage() {
  return <div className="p-6 text-lg">Sess�es anteriores aqui.</div>;
}
function AboutPage() {
  return <div className="p-6 text-lg">Sobre o Mentor 360. (Texto e foto do Alan futuramente)</div>;
}
function LoginPage() {
  return <div className="p-6 text-lg">Login/Cadastro em constru��o.</div>;
}

export default function App() {
  const [user, setUser] = useState(null);

  const handleLogout = () => setUser(null);

  return (
    <Router>
      <div className="min-h-screen bg-white flex flex-col">
        <Navbar user={user} onLogout={handleLogout} />
        <main className="flex-1 flex justify-center items-center">
          <div className="w-full max-w-2xl mx-auto">
            <Routes>
              <Route path="/" element={<ChatPage />} />
              <Route path="/sessions" element={<SessionsPage />} />
              <Route path="/about" element={<AboutPage />} />
              <Route path="/login" element={<LoginPage />} />
              <Route path="*" element={<Navigate to="/" />} />
            </Routes>
          </div>
        </main>
      </div>
    </Router>
  );
}
