import { useState } from "react";

export default function CadastroPessoasPage({ onFinalizar }) {
  const [pessoas, setPessoas] = useState([
    { nome: "", apelido: "", relacao: "", sentimento: "" }
  ]);

  function handleChange(i, campo, valor) {
    const novaLista = pessoas.map((p, idx) =>
      idx === i ? { ...p, [campo]: valor } : p
    );
    setPessoas(novaLista);
  }

  function adicionarPessoa() {
    setPessoas([...pessoas, { nome: "", apelido: "", relacao: "", sentimento: "" }]);
  }

  function removerPessoa(i) {
    setPessoas(pessoas.filter((_, idx) => idx !== i));
  }

  function handleSubmit(e) {
    e.preventDefault();
    // Aqui você pode enviar os dados ao backend futuramente
    if (onFinalizar) onFinalizar(pessoas);
    alert("Pessoas importantes cadastradas!");
  }

  return (
    <div className="max-w-xl mx-auto bg-white shadow rounded-xl p-6 mt-8">
      <h2 className="text-2xl font-bold mb-4">Pessoas Importantes</h2>
      <form onSubmit={handleSubmit}>
        {pessoas.map((pessoa, i) => (
          <div key={i} className="mb-4 border-b pb-4 last:border-b-0 last:pb-0">
            <div className="flex gap-2 mb-2">
              <input
                className="border p-2 rounded flex-1"
                placeholder="Nome"
                value={pessoa.nome}
                onChange={e => handleChange(i, "nome", e.target.value)}
                required
              />
              <input
                className="border p-2 rounded flex-1"
                placeholder="Apelido"
                value={pessoa.apelido}
                onChange={e => handleChange(i, "apelido", e.target.value)}
              />
            </div>
            <div className="flex gap-2 mb-2">
              <input
                className="border p-2 rounded flex-1"
                placeholder="Relação (ex: mãe, irmão)"
                value={pessoa.relacao}
                onChange={e => handleChange(i, "relacao", e.target.value)}
                required
              />
              <input
                className="border p-2 rounded flex-1"
                placeholder="O que sinto sobre essa pessoa"
                value={pessoa.sentimento}
                onChange={e => handleChange(i, "sentimento", e.target.value)}
              />
            </div>
            {pessoas.length > 1 && (
              <button type="button" className="text-red-500 text-xs" onClick={() => removerPessoa(i)}>
                Remover
              </button>
            )}
          </div>
        ))}
        <div className="flex justify-between items-center mt-2">
          <button type="button" className="text-blue-600" onClick={adicionarPessoa}>
            + Adicionar mais uma pessoa
          </button>
          <button
            type="submit"
            className="bg-blue-600 text-white rounded px-4 py-2 font-semibold"
          >
            Finalizar
          </button>
        </div>
      </form>
    </div>
  );
}
