import { useNavigate } from "react-router-dom";
import React, { useState } from "react";

export default function LoginPage({ onLogin }) {
  const [form, setForm] = useState({ email: "", senha: "" });
  const [erro, setErro] = useState("");
  const [carregando, setCarregando] = useState(false);
  const navigate = useNavigate();

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setErro("");
    setCarregando(true);

    if (!form.email || !form.senha) {
      setErro("Preencha todos os campos.");
      setCarregando(false);
      return;
    }

    try {
      // 1. LOGIN
      const resp = await fetch("http://localhost:3000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });
      const data = await resp.json();

      if (resp.ok && data.user_id) {
        setErro("");
        // Salva user_id e nome no localStorage
        localStorage.setItem("user_id", data.user_id);
        localStorage.setItem("user_name", data.nome);

        // 2. CRIAR SESSÃO LOGO APÓS LOGIN
        const respSessao = await fetch("http://localhost:3000/sessao", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ user_id: data.user_id, mensagem: "Início da sessão" }),
        });
        const sessaoData = await respSessao.json();
        if (respSessao.ok && sessaoData.sessao && sessaoData.sessao.id) {
          localStorage.setItem("sessao_id", sessaoData.sessao.id);
        } else {
          setErro("Erro ao criar sessão.");
          setCarregando(false);
          return;
        }

        if (onLogin) {
          onLogin({ user_id: data.user_id, nome: data.nome });
        }

        // Redireciona para o chat
        navigate("/chat");
      } else {
        setErro(data.erro || "Login ou senha incorretos.");
      }
    } catch (e) {
      setErro("Erro ao conectar com o servidor.");
    }
    setCarregando(false);
  }

  return (
    <div className="max-w-md mx-auto bg-white rounded-2xl shadow-xl p-8 mt-10">
      <h1 className="text-2xl font-bold mb-6 text-center">Entrar</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="email"
          type="email"
          placeholder="E-mail"
          value={form.email}
          onChange={handleChange}
          className="w-full border rounded-lg px-3 py-2"
          autoFocus
        />
        <input
          name="senha"
          type="password"
          placeholder="Senha"
          value={form.senha}
          onChange={handleChange}
          className="w-full border rounded-lg px-3 py-2"
        />
        <button
          type="submit"
          disabled={carregando}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 rounded-xl mt-2"
        >
          {carregando ? "Entrando..." : "Entrar"}
        </button>
      </form>
      <div className="mt-4 text-center">
        <button
          className="text-blue-600 hover:underline text-sm"
          type="button"
          onClick={() => navigate("/cadastro")}
        >
          Criar conta
        </button>
      </div>
      {erro && <div className="mt-4 text-red-600 text-center">{erro}</div>}
    </div>
  );
}
