import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Header from "./components/Header";
import ChatPage from "./pages/ChatPage";
import CadastroPage from "./pages/CadastroPage";
import CadastroPessoasPage from "./pages/CadastroPessoasPage";
import LoginPage from "./pages/LoginPage";

// Função utilitária para iniciar sessão (mantém igual)
async function iniciarSessao(user_id) {
  const res = await fetch("http://localhost:3000/sessao", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ user_id, mensagem: "Início da sessão" }),
  });
  const data = await res.json();
  if (!res.ok) throw new Error(data.erro || "Erro ao criar sessão");
  return data.sessao.id;
}

// Rota protegida: só libera o chat se tiver login e sessão no localStorage
function PrivateRoute({ children }) {
  const user_id = localStorage.getItem("user_id");
  const sessao_id = localStorage.getItem("sessao_id");
  if (!user_id || !sessao_id) {
    return <Navigate to="/login" />;
  }
  return children;
}

function App() {
  // Função para quando login for bem-sucedido (chamada pelo LoginPage)
  async function handleLogin({ user_id, nome }) {
    // Salva login no localStorage (para persistir mesmo com refresh)
    localStorage.setItem("user_id", user_id);
    localStorage.setItem("user_name", nome);
    // Cria nova sessão e salva no localStorage
    try {
      const novaSessaoId = await iniciarSessao(user_id);
      localStorage.setItem("sessao_id", novaSessaoId);
      // Depois de login e sessão, navega para o chat
      window.location.href = "/chat";
    } catch (err) {
      alert("Erro ao iniciar sessão: " + err.message);
    }
  }

  return (
    <BrowserRouter>
      <Header />
      <main className="pt-16">
        <Routes>
          <Route path="/" element={<Navigate to="/chat" />} />
          <Route
            path="/chat"
            element={
              <PrivateRoute>
                <ChatPage />
              </PrivateRoute>
            }
          />
          <Route path="/cadastro" element={<CadastroPage />} />
          <Route path="/pessoas" element={<CadastroPessoasPage />} />
          <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        </Routes>
      </main>
    </BrowserRouter>
  );
}

export default App;
