import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

export default function SessoesPage() {
  const [sessoes, setSessoes] = useState([]);
  const [carregando, setCarregando] = useState(true);
  const [erro, setErro] = useState("");

  // Busca user_id do localStorage
  const user_id = localStorage.getItem("user_id");

  useEffect(() => {
    if (!user_id) {
      setErro("Usuário não autenticado.");
      setCarregando(false);
      return;
    }

    setCarregando(true);
    fetch(`http://localhost:3000/sessoes/${user_id}`)
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data.sessoes)) {
          setSessoes(data.sessoes);
        } else {
          setErro("Não foi possível carregar sessões.");
        }
      })
      .catch(() => setErro("Erro ao buscar sessões."))
      .finally(() => setCarregando(false));
  }, [user_id]);

  return (
    <div className="max-w-2xl mx-auto bg-white rounded-2xl shadow-xl p-8 mt-10">
      <h1 className="text-2xl font-bold mb-6 text-center">Sessões Anteriores</h1>
      {carregando && <div className="text-gray-400">Carregando...</div>}
      {erro && <div className="text-red-500 text-center mb-4">{erro}</div>}

      {!carregando && sessoes.length === 0 && !erro && (
        <div className="text-gray-600 text-center">Nenhuma sessão encontrada.</div>
      )}

      {sessoes.map((sessao) => (
        <div key={sessao.id} className="mb-6 border-b pb-4 last:border-0 last:pb-0">
          <div className="text-sm text-gray-400 mb-1">
            {sessao.data_sessao
              ? new Date(sessao.data_sessao).toLocaleString("pt-BR")
              : "Data não informada"}
          </div>
          <div className="font-medium mb-2">{sessao.resumo || "Sem resumo"}</div>
          <div className="flex flex-wrap gap-2 mb-1">
            {Array.isArray(sessao.tags_tema) &&
              sessao.tags_tema.map((tag, i) => (
                <span
                  key={`tema-${sessao.id}-${i}`}
                  className="bg-emerald-100 text-emerald-700 px-2 py-0.5 rounded-full text-xs"
                >
                  {tag}
                </span>
              ))}
            {Array.isArray(sessao.tags_risco) &&
              sessao.tags_risco.map((tag, i) => (
                <span
                  key={`risco-${sessao.id}-${i}`}
                  className="bg-red-100 text-red-700 px-2 py-0.5 rounded-full text-xs"
                >
                  {tag}
                </span>
              ))}
          </div>
          <div className="text-xs text-gray-500 mb-2">
            Status: {sessao.status || "Não informado"}
          </div>
          {/* Link para abrir o chat dessa sessão */}
          <Link
            to={`/chat/${sessao.id}`}
            className="inline-block text-emerald-600 hover:underline text-sm font-semibold mt-2"
          >
            Abrir conversa completa
          </Link>
        </div>
      ))}
    </div>
  );
}
