import { useState, useRef, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import ChatBubble from "../components/ChatBubble";

const alanAvatar = "https://ui-avatars.com/api/?name=Alan+Fernandes&background=0AE68A&color=fff";

export default function ChatPage(props) {
  // Recupera sessaoId da URL caso exista
  const { sessaoId } = useParams();
  console.log("sessaoId da URL:", sessaoId); // <-- LOG ADICIONADO

  // Prioriza: URL > props > localStorage
  const user_id = props.user_id || localStorage.getItem("user_id");
  const user_name = props.user_name || localStorage.getItem("user_name") || "Você";
  const sessao_id = sessaoId || props.sessao_id || localStorage.getItem("sessao_id");
  console.log("ChatPage vai buscar histórico para sessao_id:", sessao_id); // <-- LOG ADICIONADO

  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [erro, setErro] = useState("");
  const [digitando, setDigitando] = useState(false);
  const endRef = useRef(null);
  const [resumoSessao, setResumoSessao] = useState("");


  // Salva o sessao_id atual no localStorage sempre que mudar
  useEffect(() => {
    if (sessao_id) {
      localStorage.setItem("sessao_id", sessao_id);
    }
  }, [sessao_id]);

  // Scroll automático
  useEffect(() => {
    endRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, digitando]);

  // Carrega histórico ao abrir/mudar sessão
  useEffect(() => {
    if (!sessao_id) return;
    setLoading(true);
    fetch(`http://localhost:3000/historico/${sessao_id}`)
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data.mensagens)) {
          setMessages(
            data.mensagens.map(msg => ({
              name: msg.origem === "usuario" ? user_name : "Alan",
              text: msg.texto_mensagem,
              isUser: msg.origem === "usuario",
              avatar: msg.origem === "usuario" ? undefined : alanAvatar,
            }))
          );
        } else {
          setMessages([]);
        }
      })
      .catch(() => setErro("Erro ao carregar mensagens"))
      .finally(() => setLoading(false));
  }, [sessao_id, user_name]);

  // Envia mensagem do usuário e chama IA (Alan)
  const sendMessage = async () => {
    if (input.trim() === "" || !sessao_id || !user_id) return;
    setErro("");
    // Mostra a mensagem do usuário imediatamente
    setMessages(msgs => [
      ...msgs,
      { name: user_name, text: input, isUser: true }
    ]);
    setInput("");
    setDigitando(true);

    try {
      // Salva mensagem do usuário no backend
      await fetch("http://localhost:3000/mensagem", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          sessao_id,
          user_id,
          texto_mensagem: input,
          origem: "usuario"
        })
      });

      // Chama IA (Alan)
      const resp = await fetch("http://localhost:3000/ia", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          sessao_id,
          user_id,
          mensagem: input
        })
      });
      const data = await resp.json();
      if (resp.ok && data.resposta) {
        setMessages(msgs => [
          ...msgs,
          {
            name: "Alan",
            text: data.resposta,
            isUser: false,
            avatar: alanAvatar,
          }
        ]);
      } else {
        setMessages(msgs => [
          ...msgs,
          {
            name: "Alan",
            text: "Ocorreu um erro ao responder. Tente novamente.",
            isUser: false,
            avatar: alanAvatar
          }
        ]);
      }
    } catch (err) {
      setMessages(msgs => [
        ...msgs,
        {
          name: "Alan",
          text: "Erro ao conectar com o servidor.",
          isUser: false,
          avatar: alanAvatar
        }
      ]);
    } finally {
      setDigitando(false);
    }
  };

  return (
    <div className="flex flex-col h-[70vh] bg-white rounded-2xl shadow-lg mt-8 max-w-2xl mx-auto">
      {/* Área de mensagens */}
      <div className="flex-1 overflow-y-auto p-6">
        {loading && <div className="text-gray-400">Carregando...</div>}
        {messages.map((msg, idx) => (
          <ChatBubble key={idx} {...msg} />
        ))}
        {digitando && (
          <ChatBubble
            text="Alan está digitando..."
            isUser={false}
            name="Alan"
            avatar={alanAvatar}
          />
        )}
        <div ref={endRef} />
      </div>
      {/* Input para nova mensagem */}
      <div className="border-t p-4 flex items-center gap-2">
        <input
          type="text"
          className="flex-1 border border-emerald-200 rounded-xl px-3 py-2 focus:outline-none focus:ring-2 focus:ring-emerald-400"
          placeholder="Digite sua mensagem..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendMessage()}
          disabled={loading || digitando}
        />
        <button
          onClick={sendMessage}
          className="bg-emerald-500 hover:bg-emerald-600 text-white rounded-xl px-4 py-2 font-medium transition"
          disabled={loading || !input.trim() || digitando}
        >
          Enviar
        </button>
      </div>
      {erro && <div className="text-red-500 text-center">{erro}</div>}
    </div>
  );
}
