// src/pages/CadastroPessoasPage.jsx

import React, { useState } from "react";

export default function CadastroPessoasPage({ onFinalizar }) {
  const [pessoas, setPessoas] = useState([
    { nome: "", apelido: "", relacao: "", sentimento: "" },
  ]);

  function handleChange(i, field, value) {
    const novasPessoas = [...pessoas];
    novasPessoas[i][field] = value;
    setPessoas(novasPessoas);
  }

  function adicionarPessoa() {
    setPessoas([
      ...pessoas,
      { nome: "", apelido: "", relacao: "", sentimento: "" },
    ]);
  }

  function handleSubmit(e) {
    e.preventDefault();
    // Aqui: enviar para backend, depois chamar onFinalizar()
    if (onFinalizar) onFinalizar();
  }

  return (
    <div className="max-w-xl mx-auto bg-white rounded-2xl shadow-xl p-8 mt-10">
      <h1 className="text-2xl font-bold mb-6 text-center">Pessoas Importantes</h1>
      <form onSubmit={handleSubmit}>
        {pessoas.map((pessoa, i) => (
          <div key={i} className="mb-6">
            <input
              className="w-full border rounded-lg px-3 py-2 mb-3"
              placeholder="Nome"
              value={pessoa.nome}
              onChange={e => handleChange(i, "nome", e.target.value)}
            />
            <input
              className="w-full border rounded-lg px-3 py-2 mb-3"
              placeholder="Apelido"
              value={pessoa.apelido}
              onChange={e => handleChange(i, "apelido", e.target.value)}
            />
            <input
              className="w-full border rounded-lg px-3 py-2 mb-3"
              placeholder="Relação (ex: mãe, irmão)"
              value={pessoa.relacao}
              onChange={e => handleChange(i, "relacao", e.target.value)}
            />
            <textarea
              rows={4}
              className="w-full border rounded-lg px-3 py-2"
              placeholder="Descreva com um ou dois parágrafos como você se sente sobre essa pessoa. Ex: Meu filho é a pessoa mais importante da minha vida. Ele(a) sempre foi e sempre será minha prioridade número 1."
              value={pessoa.sentimento}
              onChange={e => handleChange(i, "sentimento", e.target.value)}
            />
          </div>
        ))}
        <button
          type="button"
          className="text-blue-600 hover:underline mb-4"
          onClick={adicionarPessoa}
        >
          + Adicionar mais uma pessoa
        </button>
        <div className="text-right">
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-8 rounded-xl"
          >
            Finalizar
          </button>
        </div>
      </form>
    </div>
  );
}
