import { useState, useRef, useEffect } from "react";

// Mensagem individual
function Message({ name, text, isUser }) {
  return (
    <div className={`flex ${isUser ? "justify-end" : "justify-start"} mb-3`}>
      <div className={`rounded-2xl px-4 py-2 shadow ${isUser ? "bg-emerald-100 text-emerald-900" : "bg-gray-100 text-gray-800"} max-w-[75%]`}>
        <span className="block text-xs text-gray-400 mb-1">{name}</span>
        {text}
      </div>
    </div>
  );
}

export default function ChatPage() {
  // Mock de conversa, depois será dinâmico
  const [messages, setMessages] = useState([
    { name: "Alan", text: "Olá, seja bem-vindo ao Mentor 360! Como posso ajudar você hoje?", isUser: false },
    { name: "João", text: "Oi Alan, quero entender como lidar com minha ansiedade no trabalho.", isUser: true },
  ]);
  const [input, setInput] = useState("");
  const endRef = useRef(null);

  // Auto-scroll para a última mensagem
  useEffect(() => {
    endRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Envia nova mensagem (simulação)
  const sendMessage = () => {
    if (input.trim() === "") return;
    setMessages([...messages, { name: "João", text: input, isUser: true }]);
    setInput("");
    // Aqui futuramente vai chamar a API e adicionar a resposta do Alan
  };

  return (
    <div className="flex flex-col h-[70vh] bg-white rounded-2xl shadow-lg mt-8">
      {/* Área de mensagens */}
      <div className="flex-1 overflow-y-auto p-6">
        {messages.map((msg, idx) => (
          <Message key={idx} {...msg} />
        ))}
        <div ref={endRef} />
      </div>
      {/* Input para nova mensagem */}
      <div className="border-t p-4 flex items-center gap-2">
        <input
          type="text"
          className="flex-1 border border-emerald-200 rounded-xl px-3 py-2 focus:outline-none focus:ring-2 focus:ring-emerald-400"
          placeholder="Digite sua mensagem..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === "Enter" && sendMessage()}
        />
        <button
          onClick={sendMessage}
          className="bg-emerald-500 hover:bg-emerald-600 text-white rounded-xl px-4 py-2 font-medium transition"
        >
          Enviar
        </button>
      </div>
    </div>
  );
}
