// AppRoutes.js
import { useState } from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";
import ChatPage from "./pages/ChatPage";
import CadastroPage from "./pages/CadastroPage";
import CadastroPessoasPage from "./pages/CadastroPessoasPage";
import LoginPage from "./pages/LoginPage";
import SessoesPage from "./pages/SessoesPage";

// Função utilitária para iniciar sessão
async function iniciarSessao(user_id) {
  const res = await fetch("http://localhost:3000/sessao", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ user_id, mensagem: "Início da sessão" }),
  });
  const data = await res.json();
  if (!res.ok) throw new Error(data.erro || "Erro ao criar sessão");
  return data.sessao.id;
}

function AppRoutes() {
  const navigate = useNavigate();
  const [user, setUser] = useState(() => {
    const user_id = localStorage.getItem("user_id");
    const nome = localStorage.getItem("user_name");
    if (user_id && nome) {
      return { user_id, nome };
    }
    return null;
  });

  const [sessaoId, setSessaoId] = useState("");

  async function handleLogin({ user_id, nome }) {
    try {
      setUser({ user_id, nome });
      const novaSessaoId = await iniciarSessao(user_id);
      setSessaoId(novaSessaoId);
      localStorage.setItem("user_id", user_id);
      localStorage.setItem("user_name", nome);
      localStorage.setItem("sessao_id", novaSessaoId);
      // Redireciona para o chat só depois de tudo pronto
      navigate("/chat");
    } catch (err) {
      alert("Erro ao iniciar sessão: " + err.message);
    }
  }

  return (
    <main className="pt-16">
      <Routes>
        <Route path="/" element={<Navigate to="/chat" />} />

        {/* Chat da sessão ativa */}
        <Route
          path="/chat"
          element={
            !user || !sessaoId ? (
              <Navigate to="/login" />
            ) : (
              <ChatPage
                sessao_id={sessaoId}
                user_id={user.user_id}
                user_name={user.nome}
              />
            )
          }
        />

        {/* Chat de sessão específica via URL */}
        <Route
          path="/chat/:sessaoId"
          element={
            !user ? (
              <Navigate to="/login" />
            ) : (
              <ChatPage
                user_id={user.user_id}
                user_name={user.nome}
                // O ChatPage pega sessao_id pela URL (useParams)
              />
            )
          }
        />

        <Route path="/cadastro" element={<CadastroPage />} />
        <Route path="/pessoas" element={<CadastroPessoasPage />} />
        <Route path="/sessoes" element={<SessoesPage />} />
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />

        {/* Página 404 */}
        <Route
          path="*"
          element={
            <div style={{ padding: 32, textAlign: "center" }}>
              <h2>Página não encontrada</h2>
              <p>
                Verifique o endereço digitado ou volte para a <a href="/">página inicial</a>.
              </p>
            </div>
          }
        />
      </Routes>
    </main>
  );
}

export default AppRoutes;
