// LoginPage.js
import { useState } from "react";

// URL da API (variável de ambiente com fallback para produção)
const API_BASE_URL =
  process.env.REACT_APP_API_BASE_URL || "https://mentor360-back.onrender.com";

export default function LoginPage({ onLogin }) {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [carregando, setCarregando] = useState(false);
  const [erro, setErro] = useState("");

  async function handleSubmit(e) {
    e.preventDefault();
    setErro("");
    setCarregando(true);
    try {
      const res = await fetch(`${API_BASE_URL}/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, senha }),
      });
      const data = await res.json();

      if (!res.ok) {
        throw new Error(data?.erro || "Falha no login");
      }

      // Normaliza formatos de resposta possíveis do backend
      const user_id = data?.user?.id ?? data?.user_id ?? data?.id;
      const nome =
        data?.user?.nome ??
        data?.nome ??
        data?.user?.name ??
        data?.name ??
        "Usuário";

      if (!user_id) throw new Error("Resposta inválida do servidor (user_id).");

      // Delega para AppRoutes (que cria a sessão, salva localStorage e navega)
      onLogin({ user_id, nome });
    } catch (err) {
      setErro(err.message);
    } finally {
      setCarregando(false);
    }
  }

  return (
    <div className="max-w-md mx-auto p-6">
      <h1 className="text-2xl font-semibold mb-4">Entrar</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <label className="block">
          <span className="text-sm">E-mail</span>
          <input
            type="email"
            className="mt-1 w-full border rounded p-2"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="voce@exemplo.com"
          />
        </label>

        <label className="block">
          <span className="text-sm">Senha</span>
          <input
            type="password"
            className="mt-1 w-full border rounded p-2"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            required
            placeholder="••••••••"
          />
        </label>

        {erro && <div className="text-red-600 text-sm">{erro}</div>}

        <button
          type="submit"
          disabled={carregando}
          className="w-full bg-black text-white py-2 rounded"
        >
          {carregando ? "Entrando..." : "Entrar"}
        </button>
      </form>
    </div>
  );
}
